var mood = 0;
var url = 'http://localhost:8080';

function sendCommand(){
  var text = document.getElementById('command').value;
  var data = {text: text};

  $.post(url, data, function(resp){
      console.log("Response: " + resp);
    },
    'json'
  );


}

