var hod = require('./lib/hodneedle')
module.exports=hod

var client = new hod.HODClient('41885913-8861-4edc-8b4f-878982647379', 'v1')

client.call('analyzesentiment', {text: "I hate puppies"}, function(err, resp, body){
  var sentiment = body.aggregate.sentiment;
  var score = body.aggregate.score;
  console.log(text + "|" + sentiment + "|" + score);
});


$("#info").keyup(function(event){
  if(event.keyCode == 13){
    var text = document.getElementById('info').value;
    var data = {text: text};

    client.call('analyzesentiment', data, function(err, resp, body){
      var sentiment = body.aggregate.sentiment;
      var score = body.aggregate.score;
      console.log(text + "|" + sentiment + "|" + score);
    });
  }
});


